def bsearch(L,a,b,v,li):
	if(len(li)>=len(L)/2):
			return li
	else:
		cen=(a+b)//2
		if(L[cen]==v):
				li=listing(cen,v,li)
				return li
		elif((ord(L[cen])>(ord(v)))):
				b=cen-1
				li=listing(cen,L[cen],li)
				return bsearch(L,a,b,v,li)
		else:
			a=cen+1
			li=listing(cen,L[cen],li)
			return bsearch(L,a,b,v,li)


def listing(n,v,li):
		result=(n,v)
		li.append(result)
		return li
